import{_ as e,c as n,o,k as t,a as s}from"./chunks/framework.kkHJ_Qlh.js";const S=JSON.parse('{"title":"Section 5 연구 활동에 장애인 참여","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/2/chapter/2/section/5.md","filePath":"contents/part/2/chapter/2/section/5.md"}'),a={name:"contents/part/2/chapter/2/section/5.md"},c=t("h1",{id:"section-5-연구-활동에-장애인-참여",tabindex:"-1"},[s("Section 5 연구 활동에 장애인 참여 "),t("a",{class:"header-anchor",href:"#section-5-연구-활동에-장애인-참여","aria-label":'Permalink to "Section 5 연구 활동에 장애인 참여"'},"​")],-1),r=t("p",null,'디자인에 의한 변화(Change by Design)에서 팀 브라운(Tim Brown)은 혁신적인 제품은 관찰의 결과이며, 주류에서 벗어나 단일한 요구 사항과 선호도를 가진 사람들을 관찰함으로써 많은 것을 배울 수 있다고 제안합니다. 그는 "종형 곡선 중앙의 돌출 부분에만 집중함으로써...새롭고 놀라운 것을 배우는 것보다 이미 알고 있는 것을 확인할 가능성이 더 높습니다."라고 설명합니다.',-1),i=t("p",null,"사용자 연구 및 사용성 테스트에 장애인을 포함시키는 것은 독특해 보이지만 사용자 경험의 품질에 광범위한 영향을 미칠 수 있는 문제에 대한 통찰력을 얻을 수 있는 좋은 방법입니다. 장애인의 요구를 수용하여 탄생한 많은 디자인 사례는 진입 램프, 자동 도어 오프너, 레버 핸들, 고대비 색상 등 모든 사람의 삶의 질을 향상시켰습니다. 귀하의 연구 활동에 장애인을 포함시키면 귀하의 웹 사이트를 장애인이 보다 쉽게 접근하고 사용할 수 있도록 만드는 방법을 배우게 됩니다. 또한 청중 모두의 사용자 경험을 향상시킬 접근성 기능을 제공할 기회를 식별할 수도 있습니다.",-1),_=t("p",null,"혁신을 위한 기회 외에도 장애인을 사용자 연구에 참여시키는 데에는 많은 실질적인 이점이 있습니다. 어떤 경우에는 미국의 21세기 통신 및 비디오 접근성법(CVAA)과 영국의 영국 표준 협회 표준 bs 8878을 포함하여 장애인과의 상담을 요구하거나 권장하는 표준이 있습니다. 장애인이 참여하는 사용자 연구 활동은 의무를 이행하고 접근성 문제를 강조하는 데 도움이 됩니다. 개발팀은 이러한 문제를 해결하기 위해 노력할 수 있습니다. 해결되지 않은 문제는 프로세스 표준에 따라 기록되어야 하며 교정 및 해결을 위해 정의된 계획의 초점이 되어야 합니다.",-1),l=t("p",null,"접근성과 관련하여 WCAG(웹 콘텐츠 접근성 지침) 2.0과 같이 정의된 표준 및 지침을 준수하면 법적 의무를 충족하고 장애인의 사용성을 향상시키는 데 도움이 될 수 있습니다. 그러나 사용자 연구를 통해 표준 준수로 해결되지 않고 설계를 통해 개선될 수 있는 추가 문제를 발견할 수 있습니다. 관찰과 조사는 표준 준수를 넘어 접근 가능하고 즐거운 사용자 경험을 창출하는 솔루션을 식별하는 데 도움이 될 수 있습니다.",-1),d=t("p",null,"많은 디자이너와 개발자에게 접근성은 연결감과 공감을 불러일으키는 직접적이고 경험적인 것이 아니라 일련의 지침과 체크리스트입니다. 웹 개발 팀의 대부분의 사람들은 시각 장애인이 터치 스크린 등을 어떻게 사용하는지, 마우스를 사용하지 않는 사람이 웹 페이지에서 컨트롤을 어떻게 작동하는지 모릅니다. 이는 관심 부족이 아니라 단순히 노출 부족을 반영합니다. 사용자 연구 활동에 장애인을 참여시킴으로써 디자이너, 개발자, 전략가 및 작가는 사람들이 디지털 제품 및 서비스를 사용하는 방법, 직면하는 문제, 잘 작동하는 것과 그렇지 않은 것을 직접 확인할 수 있습니다. 대부분의 디자이너와 개발자는 문제 해결을 좋아하며 일단 문제의 영향을 알게 되면 문제 기능을 해결하려고 노력할 것입니다.",-1),p=[c,r,i,_,l,d];function h(m,u,f,x,b,k){return o(),n("div",null,p)}const $=e(a,[["render",h]]);export{S as __pageData,$ as default};
