import{_ as e,o as a,c as s,k as t,a as o}from"./chunks/framework.JgfdjA-K.js";const D=JSON.parse('{"title":"Chapter 2 디자인은 만족스럽습니다","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/3/chapter/2/section/2.md","filePath":"contents/part/3/chapter/2/section/2.md"}'),r={name:"contents/part/3/chapter/2/section/2.md"},c=t("h1",{id:"chapter-2-디자인은-만족스럽습니다",tabindex:"-1"},[o("Chapter 2 디자인은 만족스럽습니다 "),t("a",{class:"header-anchor",href:"#chapter-2-디자인은-만족스럽습니다","aria-label":'Permalink to "Chapter 2 디자인은 만족스럽습니다"'},"​")],-1),n=t("p",null,"경제학자 허버트 사이먼(Herbert Simon)은 만족하다(satisfy)와 충분하다(suffice)를 결합하여 만족하다(to satisfice)라는 용어를 만들었습니다. 만족이란 하나의 완벽한 설계 솔루션을 찾으려는 것이 아니라 모든 주요 설계 요구 사항을 대략적으로 충족(“만족”)하는 균형 잡힌 접근 방식을 목표로 의식적으로 선택하는 것입니다. 복잡하거나 오랜 시간이 걸리는 설계 반복에는 비용이 많이 들고 최적의 단일 설계 솔루션에 대한 명확한 약속 없이 알려지지 않은 여러 요소의 조합이 필연적으로 수반됩니다. 만족이 평범함에 안주하는 것처럼 들릴 수도 있지만, 만족 전략은 지난 세기에 가장 성공적인 디자인을 만들어냈습니다.",-1),i=t("p",null,"Douglas DC-3는 단일 성능 등급에서 최고의 경쟁자가 아니었습니다. 각 경쟁사는 속도, 엔진 출력, 범위 또는 운반 능력의 일부 범주에서 더 나은 경쟁자가 있었습니다. 그러나 DC-3는 설계 후 83년이 지난 오늘날 1,000개 이상의 DC-3 기체가 매일 사용되고 있을 정도로 모든 설계 요소를 성공적으로 만족시켰습니다.",-1),p=t("p",null,'사이트 디자인의 단일 지점에 대한 논쟁으로 인해 디자인 프로세스가 마비되거나 팀이 "… 모든 문제에 대한 단일 최상의 솔루션이 모든 사용자에게 존재하는지 알 수 있는 실질적인 방법이 없기 때문에 모든 프로젝트는 어느 정도 만족스럽습니다 . 완벽한 것이 아주 좋은 것의 적이 되도록 두지 마십시오.',-1),_=[c,n,i,p];function h(d,l,f,m,u,C){return a(),s("div",null,_)}const k=e(r,[["render",h]]);export{D as __pageData,k as default};
