import{_ as t,c as a,o as n,k as e,a as r}from"./chunks/framework.kkHJ_Qlh.js";const g=JSON.parse('{"title":"Chapter 3 프로젝트 관리","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/3/chapter/3/project-management.md","filePath":"contents/part/3/chapter/3/project-management.md"}'),o={name:"contents/part/3/chapter/3/project-management.md"},c=e("h1",{id:"chapter-3-프로젝트-관리",tabindex:"-1"},[r("Chapter 3 프로젝트 관리 "),e("a",{class:"header-anchor",href:"#chapter-3-프로젝트-관리","aria-label":'Permalink to "Chapter 3 프로젝트 관리"'},"​")],-1),s=e("p",null,'모든 프로젝트 관리 방법은 모든 프로젝트를 관리하는 세 가지 핵심 요소인 범위, 비용 및 일정을 수용하기 위해 어느 정도 균형을 유지하는 역할을 합니다. 웹 프로젝트에서 "범위"는 웹 사이트의 대화형 기능과 콘텐츠의 크기, 깊이, 전체 기능의 조합으로 이해할 수 있습니다. 세 가지 요소 각각은 다른 두 가지 요소와 밀접하게 연결되어 있으며, 한두 가지 요소를 강조하면 항상 세 번째 요소에 영향을 미칩니다. 예를 들어 프로젝트에서는 소규모 팀이 장기간 작업할 수 있도록 하여 적은 초기 예산의 균형을 맞출 수도 있고, 우선순위가 매우 높은 프로젝트에서는 프로젝트를 더 빨리 완료하기 위해 대규모 팀의 높은 비용을 견딜 수도 있습니다. 필연적으로 당신은 어려운 선택을 하게 됩니다. 프로젝트 관리에 있어서 가장 오래된 농담은 “프로젝트를 어떻게 원하시나요?”입니다. 좋은가요, 빠른가요, 아니면 저렴한가요? 둘 중 하나를 선택하세요.”',-1),p=e("p",null,"프로젝트 관리 분야에 대한 전체적인 논의는 이 책의 범위를 벗어나며, 관리 분야에서 경력을 쌓고자 하는 많은 미국 웹 전문가는 현재 프로젝트 관리 연구소(Project Management Institute)로부터 프로젝트 관리 전문가(PMP)로서의 공식 인증을 취득하고 있습니다. 영국과 유럽에서 가장 인기 있는 프로젝트 관리 인증은 PRINCE 2(통제된 환경의 프로젝트)입니다. PRINCE 2 또는 PMP 전문가 수준 인증은 일반적으로 완료하는 데 몇 년이 걸리며 본질적으로 프로젝트 관리 기술 분야의 석사 학위와 동일합니다. 그러나 웹 사이트 개발 및 온라인 콘텐츠 프로젝트 관리의 기본을 이해하기 위해 수년간 공부하고 연습할 필요는 없습니다.",-1),h=[c,s,p];function d(i,_,m,l,P,f){return n(),a("div",null,h)}const j=t(o,[["render",d]]);export{g as __pageData,j as default};
