import{_ as t,c as a,o as n,k as e,a as o}from"./chunks/framework.kkHJ_Qlh.js";const P=JSON.parse('{"title":"Chapter 1 모바일 인터페이스 디자인","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/7/chapter/5/mobile-interface-design.md","filePath":"contents/part/7/chapter/5/mobile-interface-design.md"}'),r={name:"contents/part/7/chapter/5/mobile-interface-design.md"},s=e("h1",{id:"chapter-1-모바일-인터페이스-디자인",tabindex:"-1"},[o("Chapter 1 모바일 인터페이스 디자인 "),e("a",{class:"header-anchor",href:"#chapter-1-모바일-인터페이스-디자인","aria-label":'Permalink to "Chapter 1 모바일 인터페이스 디자인"'},"​")],-1),c=e("p",null,'모바일 인터넷 기술은 현대 컴퓨팅 및 대중 커뮤니케이션 분야에서 가장 빠르게 성장하고 가장 흥미로운 분야 중 하나입니다. 2014년에는 미국 성인의 58%가 정교한 애플리케이션(“앱”)을 실행할 수 있는 “스마트폰”을 소유했으며, 전 세계적으로 인터넷에 연결된 장치의 수가 사람 수를 초과했습니다. 그 숫자는 2016년까지 1인당 1.4개 이상의 장치로 증가할 것으로 예상됩니다. 우리는 이를 "전화"라고 부르지만, 스마트폰과 셀룰러 지원 태블릿은 실제로 "전화 통화"도 할 수 있는 강력한 소형 컴퓨터입니다. -웨이 무선 통신, 정말. 불과 몇 년 전만 해도 강력한 데스크톱 컴퓨터만이 할 수 있었던 일을 이제 스마트폰과 태블릿에서 할 수 있지만, 우리 대부분이 소형 컴퓨터를 사용하는 경우 사용자 경험의 거의 모든 측면에 영향을 미치는 모바일 사용자에게는 실용적, 물리적, 인지적 제약이 많이 있습니다. 매일 가지고 다닙니다.',-1),i=e("p",null,'iPhone과 Android 휴대폰이 출시된 후 모바일 컴퓨팅과 스마트폰이 일반화되었을 때 대부분의 웹 디자이너와 인터페이스 전문가는 일반적인 "데스크톱" 웹 사이트를 단순화하고 단순화한 버전인 특수 모바일 전용 사이트를 옹호했습니다. 모바일 사용자는 몇 가지 기본 서비스와 정보 포인트만 필요하고 "실제"이거나 복잡한 상호 작용은 나중에 랩톱이나 데스크톱 컴퓨터에서 수행되는 "이동 중인" 혼란스럽고 주의가 산만한 사람이었습니다. 실제로 대부분의 모바일 사용자는 대부분의 사이트에서 완전한 사용자 경험을 원하고 스마트폰을 사용할 때 항상 이동 중이 아니며 웹 사용자의 약 31%는 스마트폰이 주요 컴퓨팅 장치라는 사실이 금방 명백해졌습니다.',-1),p=e("p",null,'운 좋게도 이러한 모바일 사용자의 진화하는 감각은 웹 디자인의 다른 두 가지 중요한 트렌드, 즉 CSS3의 새로운 화면 감지 및 레이아웃 기능을 사용하는 "반응형" 웹 디자인의 부상과 이를 인식하는 "모바일 우선" 콘텐츠 및 디자인 전략과 일치했습니다. 우리 대부분은 이제 사이트가 다양한 크기의 디스플레이와 다양한 사용 상황에 지능적인 방식으로 "반응"해야 하는 다중 화면, 다중 장치 세계에 살고 있습니다. 10년 전에는 Photoshop에서 고정 너비 스케치를 사용하여 새 사이트 디자인을 시작하는 것이 합리적이었을 것입니다. 그런 데스크탑 중심의 고정폭 사고는 보통 사람이 매일 다양한 크기와 모양의 화면을 사용하는 세상의 현재 현실과는 부적합합니다.',-1),d=e("p",null,'오늘날에는 연속적인 화면 크기 또는 "뷰포트" 내에서 일반적인 사용 사례 또는 "사용자 스토리"를 보는 것이 훨씬 더 합리적입니다. 여기서는 일반적으로 사람이 일어나면서 스마트워치에서 몇 개의 이메일을 빠르게 검토할 수 있습니다. 아침에 출근하는 동안 스마트폰으로 정기 뉴스 사이트를 확인하고, 두 대의 대형 모니터가 장착된 데스크톱 컴퓨터에서 일반적인 근무 시간에 수십 개의 사이트를 검토하고, 비즈니스 회의에 참석하는 동안 노트북이나 태블릿에서 더 많은 사이트를 검토할 수도 있습니다.',-1),h=[s,c,i,p,d];function l(_,m,f,u,b,g){return n(),a("div",null,h)}const k=t(r,[["render",l]]);export{P as __pageData,k as default};
