import{_ as e,c as o,o as a,k as t,a as n}from"./chunks/framework.kkHJ_Qlh.js";const S=JSON.parse('{"title":"Section 1 시선 추적 및 미적 반응","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/8/chapter/3/section/1.md","filePath":"contents/part/8/chapter/3/section/1.md"}'),s={name:"contents/part/8/chapter/3/section/1.md"},c=t("h1",{id:"section-1-시선-추적-및-미적-반응",tabindex:"-1"},[n("Section 1 시선 추적 및 미적 반응 "),t("a",{class:"header-anchor",href:"#section-1-시선-추적-및-미적-반응","aria-label":'Permalink to "Section 1 시선 추적 및 미적 반응"'},"​")],-1),r=t("p",null,'많은 시선 추적 연구에 따르면 큰 그래픽과 그래픽 요소는 "시선 고정"을 거의 유도하지 못하는 것으로 나타났습니다. 그러나 크고 심미적이며 톤을 설정하는 그래픽이 사이트에 대한 사용자의 태도에 지속적인 영향을 미치지 않는다고 실제로 결론을 내릴 수 있습니까? 웹 사용자 경험 연구의 또 다른 기관에서는 웹 사이트 사용자가 미학의 영향을 크게 받으며 질서, 아름다움, 참신함 및 창의성에 대한 긍정적인 인식이 사이트의 신뢰성과 유용성에 대한 사용자의 확신을 높인다는 사실을 보여줍니다. 최근의 디자인 작성 및 인터페이스 연구는 시각적 디자인과 사용자 연구가 어떻게 함께 작동하여 웹에서 더 나은 사용자 경험을 만들 수 있는지 보여줍니다. 탐색의 실용성과 눈과 두뇌를 즐겁게 하는 미적 인터페이스의 균형을 맞추는 경험입니다. 간단히 말해서, 아름다움이 유용성을 증가시킨다는 증거는 많이 있습니다.',-1),i=t("p",null,"시선 추적은 사용자가 정보를 보고, 해석하고, 사용하는 방식을 분석하고 이해하는 좋은 방법입니다. 그러나 시선 추적 연구를 기반으로 일부 사용자 인터페이스 연구자들은 연구 참가자가 (분명히) 웹 페이지의 큰 그래픽에 거의 주의를 기울이지 않기 때문에(눈 추적 시선 고정이 거의 없음) 큰 페이지 그래픽이 거의 영향을 미치지 않는다고 추론할 수 있다고 주장합니다. 사용자에. 여기서는 카탈로그 이미지나 상품이나 작업과 밀접하게 관련된 기타 이미지가 아니라 디자이너가 사이트의 미적 분위기를 조성하는 데 사용하는 이미지 및 기타 그래픽 콘텐츠에 관해 이야기하고 있습니다. 여기에서는 맥락이 중요합니다. 이러한 연구에서 참가자는 수행해야 할 일련의 특정 작업이 있으므로 탐색 링크, 제목, 레이블 및 버튼 및 양식 필드와 같은 인터페이스 컨트롤에 시선이 집중되는 경향이 있습니다. 표현적이거나 시각적인 톤 설정 그래픽은 이러한 작업에 거의 유용하지 않으며 이러한 작업을 수행하는 사용자가 큰 이미지를 바라보는 시선이 드물게 나타나는 것처럼 대부분의 페이지 그래픽을 (분명히) 무시한다는 것은 놀라운 일이 아닙니다.",-1),_=t("p",null,"시각적 미학의 적절한 역할에 대한 서로 다른 견해를 조화시키려면 뇌가 이미지를 처리하고 우리가 보는 것에 반응하는 방식을 이해해야 합니다.",-1),d=[c,r,i,_];function p(l,h,m,f,u,x){return a(),o("div",null,d)}const $=e(s,[["render",p]]);export{S as __pageData,$ as default};
