import{_ as t,o as a,c as r,k as e,a as s}from"./chunks/framework.JgfdjA-K.js";const x=JSON.parse('{"title":"Chapter 2 타이포그래피 디자인의 요소","description":"","frontmatter":{},"headers":[],"relativePath":"contents/part/9/chapter/2/elements-of-typographic-design.md","filePath":"contents/part/9/chapter/2/elements-of-typographic-design.md"}'),n={name:"contents/part/9/chapter/2/elements-of-typographic-design.md"},o=e("h1",{id:"chapter-2-타이포그래피-디자인의-요소",tabindex:"-1"},[s("Chapter 2 타이포그래피 디자인의 요소 "),e("a",{class:"header-anchor",href:"#chapter-2-타이포그래피-디자인의-요소","aria-label":'Permalink to "Chapter 2 타이포그래피 디자인의 요소"'},"​")],-1),c=e("p",null,'좋은 타이포그래피는 텍스트 블록, 헤드라인 및 주변 공백뿐만 아니라 한 서체와 다른 서체 간의 시각적 대비에 따라 달라집니다. 강한 대비와 독특한 패턴만큼 독자의 눈과 두뇌를 사로잡는 것은 없습니다. 페이지에 대비와 패턴을 주의 깊게 디자인해야만 이러한 특성을 얻을 수 있습니다. 모든 페이지를 촘촘한 텍스트로 가득 채우면 독자는 회색 벽을 보게 되고 본능적으로 시각적 대비가 부족한 것을 거부하게 됩니다. 단지 일률적으로 더 크게 만드는 것만으로는 도움이 되지 않습니다. 볼드체 글꼴도 빠르게 단조로워집니다. 모든 것이 굵게 표시되면 "대담하게" 눈에 띄는 것은 없습니다.',-1),p=e("p",null,'콘텐츠가 주로 텍스트인 경우 타이포그래피는 페이지의 구성 패턴을 "채색"하는 데 사용하는 도구입니다. 독자가 가장 먼저 보는 것은 페이지의 제목이나 기타 세부 사항이 아니라 페이지의 전반적인 패턴과 대비입니다. 세심하게 구성된 텍스트와 그래픽 페이지를 통해 설정된 규칙적이고 반복되는 패턴은 독자가 정보의 위치와 구성을 확립하고 가독성을 높이는 데 도움이 됩니다. 고르지 못하고 이질적인 타이포그래피와 텍스트 헤더로 인해 사용자는 반복되는 패턴과 논리적인 콘텐츠 그룹을 보기가 어렵습니다. 이러한 혼란으로 인해 익숙하지 않은 문서에서 정보가 어디에 위치할 것인지 예측하기가 어렵습니다.',-1),i=[o,c,p];function h(d,l,_,m,f,g){return a(),r("div",null,i)}const k=t(n,[["render",h]]);export{x as __pageData,k as default};
